                if (listaProductos.Count == 0)
                {
                    new MessageDialog().SendToast("Aun no haz seleccionado un producto o membres√≠a");
                }
                else
                {
                    var postCommentView = (VentaDetalleTableViewController)segue.DestinationViewController;
